const getBrowserInfo=()=>{const{userAgent:e}=navigator;let n="Unknown";return e.includes("Chrome")?n="Chrome":e.includes("Firefox")&&(n="Firefox"),{name:n,userAgent:e}},getOSInfo=()=>{const{platform:e}=navigator;return e.includes("Win")?"Windows":e.includes("Mac")?"MacOS":"Unknown OS"},getScreenInfo=()=>({width:window.screen.width,height:window.screen.height,pixelRatio:window.devicePixelRatio}),getURLInfo=()=>({url:window.location.href,referrer:document.referrer}),getAll=()=>({browser:getBrowserInfo(),os:getOSInfo(),screen:getScreenInfo(),url:getURLInfo()});module.exports={getAll:getAll};